import Die from "./components/Die"
import React from "react"
import {nanoid} from "nanoid"
import Confetti from 'react-confetti'


export default function App(){

  const [dice, setDice] = React.useState(allNewDice())
  const [tenzies, setTenzies] = React.useState(false)
  const [numRolls, setNumRolls] = React.useState(0)
  const [time, setTime] = React.useState({
    m: 0,
    s: 0
  })
  const [bestTime, setBestTime] = React.useState()


 /* Winning the game*/

  React.useEffect( ()=>{
    /*check if all dice are held and if all dice have the same value*/
    const allHeld = dice.every(dice=> dice.isHeld)
    const firstValue = dice[0].value
    const allSameValue = dice.every(dice=> dice.value === firstValue)
    if(allHeld && allSameValue){
      setTenzies(true)
    }
  }, [dice])


  function displayTime(time){
    /* 00: 00 */
    return `${time.m < 10 ? "0" : ""}${time.m}:${time.s < 10 ? "0" : ""}${time.s}`
  }


function generateNewDice(){
  return {
     value: Math.ceil(Math.random()*6),
      isHeld: false,
      id: nanoid()
  }
}


  function allNewDice(){
    const newDice = []
    for(let i=0; i<10; i++){
      newDice.push(generateNewDice()
      )
    }
    return newDice
  }

  function reset(){
    setTenzies(false)
      setDice(allNewDice())
      setNumRolls(0)
  }

  function rollDice(){
    if(!tenzies){
      setDice(oldDice=>oldDice.map(dice=>{
      return dice.isHeld ?
      dice :
      generateNewDice()
    }))
    setNumRolls(prev => prev + 1)
    } else {
      reset()
    }
  }

  function holdDice(id){
    setDice(oldDice=> oldDice.map(dice=>{
      return dice.id === id 
      ? {...dice, isHeld: !dice.isHeld}
      : dice
    }) )
  }


  const diceElements = dice.map(die => <Die key={die.id} value= {die.value} isHeld={die.isHeld} holdDice={()=>holdDice(die.id)}/>)


    return(
            <main>
              {tenzies &&
                <Confetti
                />
              }
              <h1 className="title">Tenzies</h1>
            <p className="instructions">Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
                <p className="scores">
                Rolls: {numRolls} |
                Time: {displayTime(time)} |
                {bestTime && "Best time:"} 
            </p>
                <div className="dice-container">
                {diceElements}
                </div>
                <button className="roll-dice" onClick={rollDice}>{tenzies ? "Start again" : "Roll"}</button>
                <button className="reset" onClick={reset}>Reset</button>
            </main>
    )
}
