export default function Die(props){
    const styles = {
        backgroundColor: props.isHeld ? "#59E391" : "white" 
    }

    const number = props.value

    return(

        <div 
        className="die-face" 
        style={styles}
        onClick={props.holdDice}
        >
            <div className="die-{{number}}">
                <span className="dot"></span>
            </div>
        </div>
    )
}
